PREFIX HRApop: <https://purl.humanatlas.io/graph/hra-pop>

SELECT (COUNT(DISTINCT ?as) as ?as_count)
FROM HRApop:
WHERE {
  {
  	?as ccf:has_cell_summary [ ] .
    FILTER(?as != ccf:)
  } MINUS {
    ?as a ccf:SpatialEntity .
  } MINUS {
    ?as a ccf:Dataset .
  }
}

